(389, 1, '2012-03-28 16:54:02', '2012-03-28 16:54:02', 'Students currently enrolled in the spring 2012 semester of University Writing may submit essays for consideration from Monday, April 23 to Monday, May 21, 2012. During that time, your instructor will provide you with a link to the official submission form. If you have any questions or require more information, please consult with your University Writing instructor. You may also email the editor, Glenn Michael Gordon, at <a href="mailto:gmg2119@columbia.edu">gmg2119@columbia.edu</a> .', 'Submit to TMR', '', 'inherit', 'open', 'open', '', '133-autosave', '', '', '2012-03-28 16:54:02', '2012-03-28 16:54:02', '', 133, 'http://digital.lampdev.columbia.edu/morningsidereview/?p=389', 0, 'revision', '', 0),
(443, 2, '2012-02-28 20:05:27', '2012-02-28 20:05:27', 'Students currently enrolled in the spring 2012 semester of University Writing may submit essays for consideration from Monday, April 23 to Monday, May 21, 2012. During that time, your instructor will provide you with a link to the official submission form. If you have any questions or require more information, please consult with your University Writing instructor. You may also email the editor, Glenn Michael Gordon, at <a href="mailto:gmg2119@columbia.edu">gmg2119@columbia.edu</a>.', 'Submit to TMR', '', 'inherit', 'open', 'open', '', '133-revision-10', '', '', '2012-02-28 20:05:27', '2012-02-28 20:05:27', '', 133, 'http://digital.lampdev.columbia.edu/morningsidereview/?p=443', 0, 'revision', '', 0),
